import React, { useReducer } from 'react';
import './App.css';
import ComponentB from './components/ComponentB';
import ComponentA from './components/ComponentA';
import ComponentC from './components/ComponentC';

const initialState = 0
const reducer = (state, action) => {
    switch (action) {
        case 'increment':
            return state + 1
        case 'decrement':
            return state - 1
        case 'reset':
            return initialState
        default:
            return state
    }
}

function App() {
  return (
    <div className="App">
      <ComponentA />
      <ComponentB />
      <ComponentC />
    </div>
  );
}

export default App;
